import React, { useState, useEffect } from "react";
import KpiTable from "../components/KpiTable";

export default function MyKpiPage({ empId }) { 
    const [kpiData, setKpiData] = useState(null);
    const [phase, setPhase] = useState("SelfScore"); // Employee can only self-score
    const role = "Employee"; // Fixed role

    useEffect(() => {
        async function fetchKpi() {
            const res = await fetch(`http://localhost:5038/api/kpi/3`);
            const text = await res.text(); // read raw response
            console.log("Raw response:", text);

            try {
                const data = JSON.parse(text); // attempt to parse
                setKpiData(data);
            } catch (err) {
                console.error("Failed to parse JSON:", err);
            }
        }

        fetchKpi();
    }, [empId]);

    const handleKpiChange = (compId, field, value) => {
        setKpiData((prev) => {
            const newKpi = { ...prev };
            const comp = newKpi.kpis[0].components.find(
                (c) => c.kpiComponentId === compId
            );
            if (comp) comp[field] = value;
            return newKpi;
        });
    };

    const handleSave = async () => {
        try {
            await fetch(`/api/kpi/${empId}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(kpiData),
            });
            alert("KPI saved successfully");
        } catch (err) {
            console.error(err);
            alert("Failed to save KPI");
        }
    };

    if (!kpiData) return <div>Loading your KPI...</div>;

    // Assuming only 1 KPI per month
    const currentKpi = kpiData.kpis[0];

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold mb-4">My KPI Dashboard</h2>

            <KpiTable
                kpi={currentKpi}
                role={role}
                phase={phase}
                onChange={handleKpiChange}
            />

            <button
                onClick={handleSave}
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
            >
                Save Self Score
            </button>
        </div>
    );
}
