import React, { useState, useEffect } from "react";
import axios from "axios";
import EmployeeKpiView from "./EmployeeKpiView";
import MonthYearSelector from "../components/MonthYearSelector"; // <-- adjust path if needed

const MyKpiPage = ({ empId }) => {
    const today = new Date();
    const [month, setMonth] = useState(today.getMonth() + 1);
    const [year, setYear] = useState(today.getFullYear());
    const [employeeKpi, setEmployeeKpi] = useState(null);
    const [viewMode, setViewMode] = useState("view");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const canSelfScore = (() => {
        // Only allow self-scoring after 3 days into the next month
        const firstOfNextMonth = new Date(year, month, 1);
        const threeDaysAfter = new Date(firstOfNextMonth);
        threeDaysAfter.setDate(firstOfNextMonth.getDate() + 3);
        return today >= threeDaysAfter;
    })();

    const fetchEmployeeKpi = async () => {
        try {
            setLoading(true);
            setError("");

            const response = await axios.get(
                `/api/EmployeeKpi/${empId}?month=${month}&year=${year}`
            );
            setEmployeeKpi(response.data);
        } catch (err) {
            console.error(err);
            setEmployeeKpi(null);
            setError("No KPI data found for this period.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (empId) fetchEmployeeKpi();
    }, [empId, month, year]);

    return (
        <div className="p-6">
            <h1 className="text-2xl font-bold mb-4">My KPI</h1>

            {/* Month/Year selector always visible */}
            <MonthYearSelector
                selectedMonth={month}
                selectedYear={year}
                onChange={(m, y) => {
                    setMonth(m);
                    setYear(y);
                }}
            />

            {/* Toggle button + info */}
            <div className="flex items-center gap-4 mt-4">
                <button
                    className={`px-4 py-2 rounded ${viewMode === "view"
                            ? "bg-blue-600 text-white"
                            : "bg-gray-200 text-gray-800"
                        }`}
                    onClick={() => setViewMode("view")}
                >
                    View
                </button>

                <button
                    className={`px-4 py-2 rounded ${viewMode === "self-score"
                            ? "bg-green-600 text-white"
                            : "bg-gray-200 text-gray-800"
                        }`}
                    onClick={() => canSelfScore && setViewMode("self-score")}
                    disabled={!canSelfScore}
                >
                    Self Score
                </button>

                {!canSelfScore && (
                    <p className="text-sm text-gray-500">
                        Self scoring will be available 3 days after the month starts.
                    </p>
                )}
            </div>

            {/* KPI Content */}
            {loading ? (
                <p className="mt-6 text-gray-500">Loading...</p>
            ) : error ? (
                <p className="mt-6 text-gray-500">{error}</p>
            ) : employeeKpi ? (
                <div className="mt-6">
                    <EmployeeKpiView
                        employeeKpi={employeeKpi}
                        mode={viewMode}
                        onToggleMode={() =>
                            setViewMode((prev) => (prev === "view" ? "self-score" : "view"))
                        }
                    />
                </div>
            ) : (
                <p className="mt-6 text-gray-500">No KPI data available.</p>
            )}
        </div>
    );
};

export default MyKpiPage;
