import React, { useEffect, useState } from "react";
import MonthYearSelector from "../components/MonthYearSelector";
import KpiTable from "../components/KpiTable"; // assuming you have your component table here

export default function EmployeeKPI({ empId }) {
    const [month, setMonth] = useState(new Date().getMonth() + 1);
    const [year, setYear] = useState(new Date().getFullYear());
    const [kpiData, setKpiData] = useState(null);
    const [mode, setMode] = useState("view"); // "view" | "selfscore"
    const [debugOverride, setDebugOverride] = useState(false);
    const [loading, setLoading] = useState(false);

    const fetchKpi = async () => {
        setLoading(true);
        try {
            const res = await fetch(`http://localhost:5038/api/kpi/${empId}?month=${month}&year=${year}`);
            if (!res.ok) throw new Error("Failed to load KPI");
            const data = await res.json();
            setKpiData(data);
            determineMode(data);
        } catch (err) {
            console.error(err);
            alert("Error loading KPI");
        } finally {
            setLoading(false);
        }
    };

    const determineMode = (data) => {
        if (!data || debugOverride) {
            setMode(debugOverride ? "selfscore" : "view");
            return;
        }

        const now = new Date();
        const viewedDate = new Date(data.kpi.periodYear, data.kpi.periodMonth - 1, 1);
        const diffDays = Math.abs((now - viewedDate) / (1000 * 60 * 60 * 24));

        // within 3 days from KPI month → selfscore
        if (diffDays <= 3) setMode("selfscore");
        else setMode("view");
    };

    const handleSave = async () => {
        if (!kpiData) return;
        try {
            const res = await fetch(`/api/kpi/${kpiData.kpi.kpiId}/selfscore`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(kpiData.kpi.components),
            });
            if (!res.ok) throw new Error("Failed to save");
            alert("Saved successfully!");
        } catch (err) {
            console.error(err);
            alert("Error saving self score.");
        }
    };

    const handleChange = (compId, field, value) => {
        setKpiData(prev => {
            const updated = { ...prev };
            const comp = updated.kpi.components.find(c => c.kpiComponentId === compId);
            if (comp) comp[field] = value;
            return { ...updated };
        });
    };

    return (
        <div className="p-4 space-y-4">
            <h2 className="text-xl font-bold">Employee KPI</h2>

            {/* Controls */}
            <div className="flex justify-between items-center">
                <MonthYearSelector
                    month={month}
                    year={year}
                    onChange={(m, y) => {
                        setMonth(m);
                        setYear(y);
                    }}
                />

                <div className="flex items-center gap-3">
                    <button
                        onClick={fetchKpi}
                        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                    >
                        {loading ? "Loading..." : "Load KPI"}
                    </button>

                    <label className="flex items-center gap-1 text-sm">
                        <input
                            type="checkbox"
                            checked={debugOverride}
                            onChange={e => {
                                setDebugOverride(e.target.checked);
                                determineMode(kpiData);
                            }}
                        />
                        Debug override (force selfscore)
                    </label>
                </div>
            </div>

            {/* KPI Display */}
            {!kpiData ? (
                <p className="italic text-gray-500">No KPI loaded.</p>
            ) : (
                <>
                    <div className="p-2 border rounded shadow bg-gray-50">
                        <p><strong>Employee:</strong> {kpiData.name}</p>
                        <p><strong>Month:</strong> {kpiData.kpi.periodMonth}/{kpiData.kpi.periodYear}</p>
                        <p><strong>KPI Rate:</strong> {kpiData.kpi.kpiRate}</p>
                        <p><strong>Mode:</strong> {mode}</p>
                    </div>

                    <KpiTable
                        kpi={kpiData.kpi}
                        mode={mode}
                        onChange={handleChange}
                    />

                    {mode !== "view" && (
                        <div className="flex justify-end mt-3">
                            <button
                                onClick={handleSave}
                                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                            >
                                Save
                            </button>
                        </div>
                    )}
                </>
            )}
        </div>
    );
}
