import React, { useState } from "react";
import KpiComponentTable from "../components/KpiComponentTable"; // adjust import path as needed

export default function TestKpiPage() {
    const [mode, setMode] = useState("view");
    const [components, setComponents] = useState([

    ]);

    const handleChange = (id, field, value) => {
        setComponents((prev) =>
            prev.map((c) =>
                c.kpiComponentId === id ? { ...c, [field]: value } : c
            )
        );
    };

    const handleSave = (updated) => {
        console.log("Saved Components:", updated);
        alert("Changes saved (check console).");
        setComponents(updated);
    };

    return (
        <div style={{ padding: "20px" }}>
            <h2>Test KPI Component Table</h2>

            <div style={{ marginBottom: "10px" }}>
                <label>Mode: </label>
                <select value={mode} onChange={(e) => setMode(e.target.value)}>
                    <option value="view">View</option>
                    <option value="self">Self</option>
                    <option value="edit">Edit</option>
                    <option value="assign">Assign</option>
                </select>
            </div>

            <KpiComponentTable
                components={components}
                mode={mode}
                onChange={handleChange}
                onSave={handleSave}
            />
        </div>
    );
}
