export default function KpiComponentTable({ employee, mode = "view", onChange, onSave }) {
    // ðŸ”¹ Support both PascalCase (from backend) and camelCase (frontend)
    console.log("ðŸ§­ Mode:", mode);

    const kpi = employee?.kpi || employee?.Kpi;

    // ðŸ”¹ Handle both plain arrays and `$values` lists
    const components =
        kpi?.components?.$values ||
        kpi?.Components?.$values ||
        kpi?.components ||
        kpi?.Components ||
        [];

    const handleChange = (id, field, value) => {
        if (onChange) onChange(id, field, value);
    };

    const handleAdd = () => {
        if (!onChange) return;
        onChange("add", {
            kpiComponentId: Math.floor(Math.random() * -100000), // temp id
            name: "",
            description: "",
            targetValue: 0,
            achievedValue: null,
            weight: 0,
            selfScore: null,
            assignedScore: null,
        });
    };

    const handleDelete = (id) => {
        if (!onChange) return;
        onChange("delete", id);
    };

    const isEditable = (field) => {
        if (mode === "view") return false;
        if (mode === "self") return field === "selfScore";
        if (mode === "assignScore") return field === "assignedScore";
        if (mode === "assign") return !["selfScore", "assignedScore"].includes(field);
        return false;
    };

    return (
        <div className="p-4 bg-white shadow rounded-lg">
            <h2 className="text-lg font-semibold mb-3">
                KPI Components â€” {employee?.name || employee?.Name} (
                {kpi?.periodMonth || kpi?.PeriodMonth}/{kpi?.periodYear || kpi?.PeriodYear})
            </h2>

            <table className="w-full border-collapse border border-gray-300">
                <thead className="bg-gray-100">
                    <tr>
                        <th className="border p-2">Name</th>
                        <th className="border p-2">Description</th>
                        <th className="border p-2">Target</th>
                        <th className="border p-2">Achieved</th>
                        <th className="border p-2">Weight</th>
                        <th className="border p-2">Self Score</th>
                        <th className="border p-2">Assigned Score</th>
                        {mode === "assign" && <th className="border p-2">Action</th>}
                    </tr>
                </thead>
                <tbody>
                    {components.map((comp) => (
                        <tr key={comp.kpiComponentId || comp.KpiComponentId}>
                            {[
                                ["name", comp.name ?? comp.Name],
                                ["description", comp.description ?? comp.Description],
                                ["targetValue", comp.targetValue ?? comp.TargetValue],
                                ["achievedValue", comp.achievedValue ?? comp.AchievedValue],
                                ["weight", comp.weight ?? comp.Weight],
                                ["selfScore", comp.selfScore ?? comp.SelfScore],
                                ["assignedScore", comp.assignedScore ?? comp.AssignedScore],
                            ].map(([field, value]) => (
                                <td key={field} className="border p-2 text-center">
                                    {isEditable(field) ? (
                                        <input
                                            type={
                                                ["targetValue", "achievedValue", "weight", "selfScore", "assignedScore"].includes(field)
                                                    ? "number"
                                                    : "text"
                                            }
                                            className="border rounded px-2 py-1 w-full"
                                            value={value ?? ""}
                                            onChange={(e) =>
                                                handleChange(comp.kpiComponentId || comp.KpiComponentId, field, e.target.value)
                                            }
                                        />
                                    ) : (
                                        value ?? "-"
                                    )}
                                </td>
                            ))}
                            {mode === "assign" && (
                                <td className="border p-2 text-center">
                                    <button
                                        onClick={() =>
                                            handleDelete(comp.kpiComponentId || comp.KpiComponentId)
                                        }
                                        className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                                    >
                                        Delete
                                    </button>
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </table>

            {mode === "assign" && (
                <button
                    onClick={handleAdd}
                    className="mt-3 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                    Add Component
                </button>
            )}

            {mode !== "view" && (
                <div className="mt-4 text-right">
                    <button
                        onClick={onSave}
                        className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
                    >
                        Save
                    </button>
                </div>
            )}
        </div>
    );
}
