import React, { useState, useEffect } from "react";
import KpiTable from "../components/KpiTable";
import MonthYearSelector from "../components/MonthYearSelector";

export default function MyKpiPage({ empId }) {
    const role = "Employee";

    const [kpiData, setKpiData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [phase, setPhase] = useState("ViewOnly");

    const [month, setMonth] = useState(new Date().getMonth() + 1);
    const [year, setYear] = useState(new Date().getFullYear());

    useEffect(() => {
        async function fetchKpi() {
            try {
                setLoading(true);
                const res = await fetch(`http://localhost:5038/api/kpi/${empId}?month=${month}&year=${year}`);
                if (!res.ok) throw new Error(`Failed to fetch: ${res.statusText}`);
                const data = await res.json();

                const kpisArray = data.kpis?.$values ?? [];
                const kpis = kpisArray.map((k) => ({
                    ...k,
                    components: k.components?.$values ?? [],
                }));

                setKpiData({ ...data, kpis });
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        }

        if (empId) fetchKpi();
    }, [empId, month, year]);

    // Determine if self scoring is open (within 3 days after month end)
    useEffect(() => {
        const now = new Date();
        const targetMonthEnd = new Date(year, month, 0); // last day of selected month
        const diffDays = Math.floor((now - targetMonthEnd) / (1000 * 60 * 60 * 24));
        const isSelfScoreWindow = diffDays >= 0 && diffDays <= 3;

        setPhase(isSelfScoreWindow ? "SelfScore" : "ViewOnly");
    }, [month, year]);

    const handleKpiChange = (compId, field, value) => {
        setKpiData((prev) => {
            if (!prev) return prev;
            const newComponents = prev.kpis[0].components.map((comp) =>
                comp.kpiComponentId === compId ? { ...comp, [field]: value } : comp
            );
            const newKpis = [...prev.kpis];
            newKpis[0] = { ...newKpis[0], components: newComponents };
            return { ...prev, kpis: newKpis };
        });
    };

    const handlePhaseToggle = () => {
        setPhase((prev) => (prev === "SelfScore" ? "ViewOnly" : "SelfScore"));
    };

    if (loading) return <div>Loading KPI data...</div>;
    if (error) return <div className="text-red-600">Error: {error}</div>;
    if (!kpiData || !kpiData.kpis?.length) return <div>No KPI data available.</div>;

    const currentKpi = kpiData.kpis[0];

    return (
        <div className="p-4">
            <div className="flex items-center justify-between mb-4">
                <MonthYearSelector
                    month={month}
                    year={year}
                    onMonthChange={setMonth}
                    onYearChange={setYear}
                    onViewClick={() => { }} // not used here
                />

                <button
                    onClick={handlePhaseToggle}
                    className={`px-3 py-1 rounded font-medium ${phase === "SelfScore"
                            ? "bg-blue-500 text-white"
                            : "bg-gray-300 text-black"
                        }`}
                >
                    {phase === "SelfScore" ? "Switch to View Only" : "Switch to Self Score"}
                </button>
            </div>

            <KpiTable
                kpi={currentKpi}
                role={role}
                phase={phase}
                onChange={handleKpiChange}
            />
        </div>
    );
}
