import React, { useState, useEffect } from "react";
import KpiTable from "../components/KpiTable";

export default function MyKpiPage({ empId }) {
    const role = "Employee";

    const [kpiData, setKpiData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchKpi() {
            try {
                setLoading(true);
                const res = await fetch(`http://localhost:5038/api/kpi/9`);
                if (!res.ok) throw new Error(`Failed to fetch: ${res.statusText}`);
                const data = await res.json();

                const kpisArray = data.kpis?.$values ?? [];
                const kpis = kpisArray.map((k) => ({
                    ...k,
                    components: k.components?.$values ?? [],
                }));

                setKpiData({ ...data, kpis });
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        }
        if (empId) fetchKpi();
    }, [empId]);

    const handleKpiChange = (compId, field, value) => {
        setKpiData((prev) => {
            if (!prev) return prev;
            const newComponents = prev.kpis[0].components.map((comp) =>
                comp.kpiComponentId === compId ? { ...comp, [field]: value } : comp
            );
            const newKpis = [...prev.kpis];
            newKpis[0] = { ...newKpis[0], components: newComponents };
            return { ...prev, kpis: newKpis };
        });
    };

    if (loading) return <div>Loading KPI data...</div>;
    if (error) return <div className="text-red-600">Error: {error}</div>;
    if (!kpiData || !kpiData.kpis?.length) return <div>No KPI data available.</div>;

    const currentKpi = kpiData.kpis[0];
    const phase = "SelfScore";

    return (
        <div className="p-4">
            <KpiTable
                kpi={currentKpi}
                role={role}
                phase={phase}
                onChange={handleKpiChange}
            />
        </div>
    );
}
