import React, { useState, useEffect } from "react";
import "./Calendar.css";

export default function Calendar() {
    const now = new Date();
    const defaultMonth = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, "0")}`;
    const [month, setMonth] = useState(defaultMonth);
    const [selectedDay, setSelectedDay] = useState(null);
    const [attendanceData, setAttendanceData] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const [yearStr, monthStr] = month.split("-");
    const year = parseInt(yearStr, 10);
    const monthIndex = parseInt(monthStr, 10) - 1;

    const daysInMonth = new Date(year, monthIndex + 1, 0).getDate();
    const firstDay = new Date(year, monthIndex, 1).getDay();
    const startOffset = firstDay === 0 ? 6 : firstDay - 1;

    const weeks = [];
    let currentDay = 1 - startOffset;
    while (currentDay <= daysInMonth) {
        const week = [];
        for (let i = 0; i < 7; i++) {
            week.push(currentDay > 0 && currentDay <= daysInMonth ? currentDay : null);
            currentDay++;
        }
        weeks.push(week);
    }

    const onMonthChange = (e) => {
        setMonth(e.target.value);
        setSelectedDay(null);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                setError("");

                const [y, m] = month.split("-");
                const url = `http://localhost:5038/api/clockin/employee/4?month=${parseInt(m)}&year=${y}`;
                const res = await fetch(url);
                if (!res.ok) throw new Error(`HTTP ${res.status}`);

                const json = await res.json(); // assume plain array
                const result = {};

                json.forEach((record) => {
                    const dateStr = record.date.split("T")[0];
                    if (record.dailyRecords && Array.isArray(record.dailyRecords)) {
                        record.dailyRecords.forEach((dayRecord) => {
                            const key = `${year}-${String(m).padStart(2, "0")}-${String(dayRecord.day).padStart(2, "0")}`;
                            result[key] = {
                                score: dayRecord.actual,
                                times: Array.isArray(dayRecord.logs) ? dayRecord.logs : [],
                            };
                        });
                    }
                });

                setAttendanceData(result);
            } catch (err) {
                console.error(err);
                setError("Không thể tải dữ liệu chấm công.");
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [month]);

    return (
        <div className="amps-wrapper">
            <h2 className="amps-title">📅 Check Attendance</h2>

            <div className="amps-filter">
                <label>Chọn tháng:</label>
                <input
                    className="amps-input-month"
                    type="month"
                    value={month}
                    onChange={onMonthChange}
                />
            </div>

            {loading && <p>Đang tải dữ liệu...</p>}
            {error && <p className="error-text">{error}</p>}

            <div className="amps-calendar-container">
                <div className="amps-weekdays">
                    <div>T2</div>
                    <div>T3</div>
                    <div>T4</div>
                    <div>T5</div>
                    <div>T6</div>
                    <div>T7</div>
                    <div>CN</div>
                </div>

                <div className="amps-calendar-grid">
                    {weeks.map((week, wi) =>
                        week.map((day, di) => {
                            if (!day) return <div key={`${wi}-${di}`} className="day-empty" />;

                            const dateKey = `${month}-${String(day).padStart(2, "0")}`;
                            const data = attendanceData[dateKey];
                            const isSelected = selectedDay?.dateKey === dateKey;

                            return (
                                <div
                                    key={`${wi}-${di}`}
                                    className={`day-cell ${isSelected ? "selected" : ""}`}
                                    onClick={() => setSelectedDay({ dateKey, data })}
                                >
                                    <div className="day-number">{day}</div>
                                    <div className="day-score">{data?.score ?? ""}</div>
                                </div>
                            );
                        })
                    )}
                </div>
            </div>

            {selectedDay && (
                <div className="amps-times-card">
                    <div className="times-header">
                        <strong>Ngày {selectedDay.dateKey}</strong>
                        <span className="score-pill">Điểm: {selectedDay.data?.score ?? "—"}</span>
                    </div>

                    <ul className="times-list">
                        {selectedDay.data?.times?.length ? (
                            selectedDay.data.times.map((t, i) => (
                                <li key={i} className="time-item">⏰ {t}</li>
                            ))
                        ) : (
                            <li className="time-empty">Không có dữ liệu</li>
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
}
