import React, { useState, useEffect } from "react";
import KpiTable from "../components/KpiTable";

export default function MyKpiPage({ empId }) { 
    const [kpiData, setKpiData] = useState(null);
    const [phase, setPhase] = useState("SelfScore"); // Employee can only self-score
    const [error, setError] = useState(null);
    const role = "Employee"; // Fixed role

    useEffect(() => {
        async function fetchKpi() {
            const res = await fetch(`http://localhost:5038/api/kpi/9`);
            const text = await res.text(); // read raw response
            console.log("Raw response:", text);

            try {
                const data = JSON.parse(text); // attempt to parse
                setKpiData(data);
            } catch (err) {
                console.error("Failed to parse JSON:", err);
            }
        }

        fetchKpi();
    }, [empId]);

    const handleKpiChange = (compId, field, value) => {
        setKpiData((prev) => {
            if (!prev) return prev;
            const newComponents = prev.kpis[0].components.map((comp) =>
                comp.kpiComponentId === compId ? { ...comp, [field]: value } : comp
            );
            const newKpis = [...prev.kpis];
            newKpis[0] = { ...newKpis[0], components: newComponents };
            return { ...prev, kpis: newKpis };
        });
    };

    const handleSave = async () => {
        try {
            await fetch(`/api/kpi/${empId}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(kpiData),
            });
            alert("KPI saved successfully");
        } catch (err) {
            console.error(err);
            alert("Failed to save KPI");
        }
    };

    if (loading) return <div>Loading KPI data...</div>;
    if (error) return <div className="text-red-600">Error: {error}</div>;
    if (!kpiData || !kpiData.kpis?.length) return <div>No KPI data available.</div>;

    const currentKpi = kpiData.kpis[0];

    // Determine current phase (example logic)
    //let phase = "SelfScore"; // you can compute based on date / role / assigned
    if (role === "Head") phase = currentKpi.assignedScore != null ? "Finalize" : "Assign";

    return (
        <div className="p-4">
            <KpiTable
                kpi={currentKpi}
                role={role}
                phase={phase}
                onChange={handleKpiChange}
            />
        </div>
    );
}
