import React from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";

import Header from "./components/Header";
import CheckAttendance from "./components/Calendar";
import ButtonDemo from "./components/ButtonDemo";
import Dashboard from "./pages/Dashboard";
import ViewAdjustmentRequests from "./components/ViewAdjustmentRequests";
import CustomButton from "./components/CustomButton";

import Login from "./pages/Login";
import Welcome from "./pages/Welcome";
import LeaveRequest from "./pages/LeaveRequest";

import "./App.css";

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<LoginWrapper />} />

                <Route
                    path="/dashboard"
                    element={
                        <ProtectedRoute>
                            <Dashboard />
                        </ProtectedRoute>
                    }
                />

                <Route
                    path="/attendance"
                    element={
                        <ProtectedRoute>
                            <AttendancePage />
                        </ProtectedRoute>
                    }
                />

                <Route
                    path="/leave-request"
                    element={
                        <ProtectedRoute>
                            <LeaveRequest />
                        </ProtectedRoute>
                    }
                />

                <Route path="/welcome" element={<WelcomeWrapper />} />
                <Route path="/dashboard" element={<DashboardWrapper />} />
                <Route path="/leave" element={<LeaveWrapper />} />
            </Routes>
        </Router>
    );
}

// Wrappers handle navigation logic

function ProtectedRoute({ children }) {
    const token = localStorage.getItem("token");
    if (!token) {
        return <Navigate to="/login" replace />;
    }
    return children;
}

function LoginWrapper() {
    const navigate = useNavigate();
    return (
        <Login
            onLogin={(uname) => {
                localStorage.setItem("username", uname);
                navigate("/dashboard");
            }}
        />
    );
}

function WelcomeWrapper() {
    const navigate = useNavigate();
    const username = localStorage.getItem("username") || "";
    return (
        <Welcome
            username={username}
            onContinue={() => navigate("/dashboard")}
        />
    );
}

function DashboardWrapper() {
    const navigate = useNavigate();
    return (
        <Dashboard
            onLeaveRequest={() => navigate("/leave")}
            onLogout={() => {
                alert("Đăng xuất thành công!");
                localStorage.removeItem("username");
                navigate("/");
            }}
        />
    );
}

function LeaveWrapper() {
    const navigate = useNavigate();
    return <LeaveRequest onBack={() => navigate("/dashboard")} />;
}

export default App;
